INSERT IGNORE INTO fw_modules (name, title, section, status) VALUES ('regions', 'Регионы', 'front_support', '1');
DROP TABLE IF EXISTS `fw_citys`;
CREATE TABLE IF NOT EXISTS `fw_citys` (`id` int(11) NOT NULL auto_increment,`reg_id` int(11) NOT NULL default '0',`name` varchar(255) NOT NULL default '',`description` text ,`status` tinyint(1) default '1',PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=cp1251 AUTO_INCREMENT=1 ;
DROP TABLE IF EXISTS `fw_firms`;
CREATE TABLE IF NOT EXISTS `fw_firms` (`id` int(11) NOT NULL auto_increment,`name` varchar(255) NOT NULL default '',`logo` varchar(15) default NULL,`address` varchar(255) default NULL,`email` varchar(100) default NULL,`phone` varchar(50),`site` varchar(100) default NULL,`city_id` int(11) NOT NULL default '0',`metro_id` int(11) default NULL,`description` text ,`status` tinyint(1) NOT NULL default '1',PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=cp1251 AUTO_INCREMENT=1 ;
DROP TABLE IF EXISTS `fw_firms_metros`;
CREATE TABLE IF NOT EXISTS `fw_firms_metros` (`id` int(11) NOT NULL auto_increment,`firm_id` int(11) NOT NULL default '0',`metro_id` int(11) NOT NULL default '0',PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=cp1251 AUTO_INCREMENT=1 ;
DROP TABLE IF EXISTS `fw_metros`;
CREATE TABLE IF NOT EXISTS `fw_metros` (`id` int(11) NOT NULL auto_increment,`city_id` int(11) NOT NULL default '0',`name` varchar(255) NOT NULL default '',`description` text ,`status` tinyint(1) default '1',PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=cp1251 AUTO_INCREMENT=1 ;
DROP TABLE IF EXISTS `fw_regions`;
CREATE TABLE IF NOT EXISTS `fw_regions` (`id` int(11) unsigned NOT NULL auto_increment,`param_left` int(11) NOT NULL default '0',`param_right` int(11) NOT NULL default '0',`param_level` int(11) NOT NULL default '0',`name` varchar(255) NOT NULL default '',`description` text NOT NULL,`status` tinyint(1) NOT NULL default '1',`url` varchar(255) default NULL,PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=cp1251 AUTO_INCREMENT=1 ;